import React from 'react';
import './App.css';
import Bar from './bar';

const listSize        = 50;
const delayTime       = 30;
const defaultColor    = '#61dafb';
const accessListColor = '#f82aff';
const swapItemColor   = '#34d492'
// const swapItemColor   = '#00ff9d';

function fillArray(value, len) {
    var arr = new Array(len);;
    for (var i = 0; i < len; i++) {
      arr[i] = value;
    }
    return arr;
  }

class Visualizer extends React.Component {
    constructor(){
        super();
        this.state = {
            array: Array.from(Array(listSize).keys()),
            colors: fillArray(defaultColor, listSize)
        }
        const {array}    = this.state; 
        this.totalWidth  = 70;
        this.totalHeight = 60;
        this.barWidth    = this.totalWidth/array.length;
        this.barHeight   = this.totalHeight/array.length;
    }

    bubbleSort(arr){
        let tempArr = JSON.parse(JSON.stringify(arr));
        let states = [];
        let colors = [];
        let delays = [];
        let iter   = 0;
        for(let i = 0; i<arr.length; i++){
            for(let j = 0; j<arr.length-i-1; j++){
                iter++;
                let color  = fillArray(defaultColor, listSize);
                color[j]   = accessListColor;
                // color[j+1] = accessListColor;
                colors.push(color);
                states.push(JSON.parse(JSON.stringify(tempArr)));
                delays.push(iter);
                if(tempArr[j]>tempArr[j+1]){
                    iter++;
                    let temp     = tempArr[j];
                    tempArr[j]   = tempArr[j+1];
                    tempArr[j+1] = temp;
                    let color    = fillArray(defaultColor, listSize);
                    color[j+1]     = swapItemColor;
                    // color[j]   = swapItemColor;
                    colors.push(color);
                    states.push(JSON.parse(JSON.stringify(tempArr)));
                    delays.push(iter);
                }
            }
        }
        states.push(states[states.length - 1]);
        colors.push(fillArray(defaultColor, listSize));
        delays.push(iter+1);
        for(let i=0; i<states.length; i++){
            setTimeout(()=>{this.setState(
                    { array: states[i], colors: colors[i] }
                )}, delays[i]*delayTime);
        }
    }
    
    insertionSort(arr){
        let tempArr = JSON.parse(JSON.stringify(arr));
        let states = [];
        let colors = [];
        let delays = [];
        let iter   = 0;
        for(let i = 1; i<arr.length; i++){
            for(let j = i; j>=0; j--){
                iter++;
                let color  = fillArray(defaultColor, listSize);
                color[j]   = accessListColor;
                // color[j+1] = accessListColor;
                colors.push(color);
                states.push(JSON.parse(JSON.stringify(tempArr)));
                delays.push(iter);
                if(tempArr[j]<tempArr[j-1]){
                    iter++;
                    let temp     = tempArr[j];
                    tempArr[j]   = tempArr[j-1];
                    tempArr[j-1] = temp;

                    let color    = fillArray(defaultColor, listSize);
                    color[j-1]   = swapItemColor;
                    // color[j+1]   = swapItemColor;
                    colors.push(color);
                    states.push(JSON.parse(JSON.stringify(tempArr)));
                    delays.push(iter);
                }
                else{
                    break;
                }

            }
        }
        states.push(states[states.length - 1]);
        colors.push(fillArray(defaultColor, listSize));
        delays.push(iter+1);
        for(let i=0; i<states.length; i++){
            setTimeout(()=>{this.setState(
                    { array: states[i], colors: colors[i] }
                )}, delays[i]*delayTime);
        }
    }

    quickSort(array, start, end, iter){
        let tempArr = JSON.parse(JSON.stringify(array));
        let states = [];
        let colors = [];
        let delays = [];

        let arr = tempArr.slice(start, end-start);
        if (arr.length < 2){
            return arr;
        }
        let pivot = tempArr[end-1];
        let pivot_index = end-1;
        let i = start;
        let swaps = 0;
        while (i < pivot_index){
            let num = tempArr[i];
            iter+=1;
            let color  = fillArray(defaultColor, listSize);
            color[i]   = accessListColor;
            colors.push(color);
            states.push(JSON.parse(JSON.stringify(tempArr)));
            delays.push(iter);
            if (num > pivot){
                iter+=1;
                tempArr.splice(i, 1);
                tempArr.splice(pivot_index, 0, num);
                pivot_index-=1;
                swaps+=1;
                let color  = fillArray(defaultColor, listSize);
                color[i]   = swapItemColor;
                colors.push(color);
                states.push(JSON.parse(JSON.stringify(tempArr)));
                delays.push(iter);
            }
            else{
                i+=1;
            }
        }
        states.push(states[states.length - 1]);
        colors.push(fillArray(defaultColor, listSize));
        delays.push(iter+1);
        for(let i=0; i<states.length; i++){
            setTimeout(()=>{this.setState(
                    { array: states[i], colors: colors[i] }
                )}, delays[i]*delayTime);
        }
        let result = this.quickSort(tempArr, start, pivot_index, iter).concat([pivot])
        result.concat(this.quickSort(tempArr, pivot_index+1, pivot_index+swaps+1));
        console.log("THIS IS THE RESULT");
        console.log(states);
    }

    shuffle(arr) {
        for (var i = arr.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        this.setState({array: arr});
        console.log(this.state)
    }

    render (){
        const {array, colors} = this.state;
        const auxList = Array.from(Array(listSize).keys());

        return (<div className="visualizer-container">
            <div className="array-container">
                {auxList.map(index => (
                    <Bar key={index} width={this.barWidth -0.5} height={this.barHeight * (array[index]+1)} color={colors[index]}/>
                ))
                }
            </div>
            <div className="button-container">
                <button className="button" onClick={()=> {this.shuffle(array);}}>Shuffle Array</button>
                <button className="button" onClick={()=> {this.bubbleSort(array);}}>BubbleSort</button>
                <button className="button" onClick={()=> {this.insertionSort(array)}}>Insertion Sort</button>
                <button className="button" onClick={()=> {alert('Need to finish this');this.quickSort(array, 0, array.length, 1)}}>Quick Sort</button>
            </div>
      </div>);
    };
  }
  
  export default Visualizer;
