import React, {useState} from 'react';
import './App.css';
import Bar from './bar';

class Visualizer extends React.Component {
    constructor(props){
        super (props);
        this.state = {
            array: useState(Array.from(Array(40).keys()))
        }
        const {array} = this.state;
        this.totalWidth = 70;
        this.totalHeight = 60;
        this.barWidth = this.totalWidth/array.length;
        this.barHeight = this.totalHeight/array.length;
        this.tempArr = JSON.parse(JSON.stringify(array));
    }

    

    shuffle(arr) {
        for (var i = arr.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        this.setState({arr});
    }

    render (){
        return (<div className="visualizer-container">
            <div className="array-container">
                {this.array.map(bar => (
                    <Bar key={bar} width={this.barWidth -0.5} height={this.barHeight * (bar+1)}/>
                ))
                }
            </div>
            <div className="button-container">
                <button className="button" onClick={()=> {this.shuffle(this.tempArr);}}>Shuffle Array</button>
                <button className="button" onClick={()=> {alert('Merge Sort')}}>Merge Sort</button>
            </div>
      </div>);
    };
  }
  
  export default Visualizer;
