import React, {useState} from 'react';
import './App.css';
import Bar from './bar';
import buttonContainer from './button-container';

function Visualizer() {

    const [array, setArray] = useState(Array.from(Array(40).keys()));
    let totalWidth = 70;
    let totalHeight = 60;
    let barWidth = totalWidth/array.length;
    let barHeight = totalHeight/array.length;
    let tempArr = JSON.parse(JSON.stringify(array));

    function shuffle(arr) {
        for (var i = arr.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        setArray(arr);
    }

    return (
        <div className="visualizer-container">
            <div className="array-container">
                { array.map(bar => (
                    <Bar key={bar} width={barWidth -0.5} height={barHeight * (bar+1)}/>
                ))
                }
                <buttonContainer functions={[() => {shuffle(tempArr);}, () => {alert('Merge Sort');}]}/>
            </div>
            
      </div>
    );
  }
  
  export default Visualizer;
