import React from 'react';
import './App.css';
import Bar from './bar';

const listSize = 20;
const delayTime = 50;

class Visualizer extends React.Component {
    constructor(){
        super();
        this.state = {
            array: Array.from(Array(listSize).keys())
        }
        const {array} = this.state; 
        this.totalWidth = 70;
        this.totalHeight = 60;
        this.barWidth = this.totalWidth/array.length;
        this.barHeight = this.totalHeight/array.length;
    }

    bubbleSort(arr){
        let tempArr = JSON.parse(JSON.stringify(arr));
        let states = [];
        let delays = [];
        let iter = 0;
        for(let i = 0; i<arr.length; i++){
            for(let j = 0; j<arr.length-i-1; j++){
                iter+=1;
                if(tempArr[j]>tempArr[j+1]){
                    let temp = tempArr[j];
                    tempArr[j] = tempArr[j+1];
                    tempArr[j+1] = temp;
                    states.push(JSON.parse(JSON.stringify(tempArr)));
                    delays.push(iter);
                }

            }
        }
        for(let i=0; i<states.length; i++){
            setTimeout(()=>{this.setState(
                    { array: states[i] }
                )}, delays[i]*delayTime);
        }
    }
    
    insertionSort(arr){
        let tempArr = JSON.parse(JSON.stringify(arr));
        let states = [];
        let delays = [];
        let iter = 0;
        for(let i = 1; i<arr.length; i++){
            for(let j = i; j>=0; j--){
                iter+=1;
                if(tempArr[j]<tempArr[j-1]){
                    let temp = tempArr[j];
                    tempArr[j] = tempArr[j-1];
                    tempArr[j-1] = temp;
                    states.push(JSON.parse(JSON.stringify(tempArr)));
                    delays.push(iter);
                }
                else{
                    break;
                }

            }
        }
        for(let i=0; i<states.length; i++){
            setTimeout(()=>{this.setState(
                    { array: states[i] }
                )}, delays[i]*delayTime);
        }
    }

    quickSort(arr){
        let tempArr = JSON.parse(JSON.stringify(arr));
        let states = [];
        let delays = [];
        let iter = 0;

        for(let i=0; i<states.length; i++){
            setTimeout(()=>{this.setState(
                    { array: states[i] }
                )}, delays[i]*delayTime);
        }
    }

    shuffle(arr) {
        for (var i = arr.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        this.setState({array: arr});
    }

    render (){
        const {array} = this.state;
        return (<div className="visualizer-container">
            <div className="array-container">
                {array.map(bar => (
                    <Bar key={bar} width={this.barWidth -0.5} height={this.barHeight * (bar+1)}/>
                ))
                }
            </div>
            <div className="button-container">
                <button className="button" onClick={()=> {this.shuffle(array);}}>Shuffle Array</button>
                <button className="button" onClick={()=> {this.bubbleSort(array);}}>BubbleSort</button>
                <button className="button" onClick={()=> {this.insertionSort(array)}}>Insertion Sort</button>
                <button className="button" onClick={()=> {alert('Need to finish this');this.quickSort(array,0, array.length-1, 1)}}>Quick Sort</button>
            </div>
      </div>);
    };
  }
  
  export default Visualizer;
