import React, {useState} from 'react';
import './App.css';
import Bar from './bar';

function Visualizer() {

    const [array, setArray] = useState(Array.from(Array(150).keys()));
    let totalWidth = 70;
    let totalHeight = 500;
    let barWidth = totalWidth/array.length;
    let barHeight = totalHeight/array.length;

    return (
        <div className="visualizer-container">
            <div className="array-container">
                { array.map(bar => (
                    <Bar width={barWidth -2} height={barHeight * (bar+1)}/>
                ))
                }
                
            </div>
      </div>
    );
  }
  
  export default Visualizer;
