import React, {useState} from 'react';
import './App.css';
import Bar from './bar';

function Visualizer() {

    const [array, setArray] = useState(Array.from(Array(40).keys()));
    let totalWidth = 70;
    let totalHeight = 60;
    let barWidth = totalWidth/array.length;
    let barHeight = totalHeight/array.length;

    function shuffle(arr) {
        for (var i = arr.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        return arr;
    }

    return (
        <div className="visualizer-container">
            <div className="array-container">
                { array.map(bar => (
                    <Bar width={barWidth -0.5} height={barHeight * (bar+1)}/>
                ))
                }
                
            </div>
            <button onClick={setArray()}>Shufle Array</button>/>
      </div>
    );
  }
  
  export default Visualizer;
