import React, {useState} from 'react';
import './App.css';
import Bar from './bar';

function Visualizer() {

    const [array, setArray] = useState([9, 8, 7, 6, 5, 4, 3, 2, 1, 0]);
    let totalWidth = 70;
    let totalHeight = 500;
    let barWidth = totalWidth/array.length;
    let barHeight = totalHeight/array.length;

    return (
        <div className="visualizer-container">
            <div className="array-container">
                { array.map(bar => (
                    <Bar width={barWidth} height={barHeight * (array+1)}/>
                ))
                }
                
            </div>
      </div>
    );
  }
  
  export default Visualizer;
